const utils_textToHex = (text) => {
    if(text == "") 
      return "0x00";
    var hex = "0x";
    for (var i = 0; i < text.length; i++) {
      var s = text.charCodeAt(i).toString(16);
      while (s.length < 2) {
        s = '0' + s;
      }
      hex += s;
    }
    return hex;
}

const getNodeHomeAsync = async(network, toNetwork, contract_address, typed) => {
    networkSettings(network);

    try {
        const provider = new ethers.providers.JsonRpcProvider(rpcUrl);
        const toNetworkHex = utils_textToHex(toNetwork);

        const bridgeConfigContract = new ethers.Contract(
            bridgeConfigContractAddress,
            abiBridgeConfig,
            provider
        );

        const customNhidResponse = await bridgeConfigContract.customNhid(contract_address);

        const customNhid = BigInt(customNhidResponse).toString(16);

        if (customNhid != "0") {
            const getTokNetworkResponse = await bridgeConfigContract.getToNetwork(contract_address, toNetworkHex);

            const getTokNetwork = parseInt(getTokNetworkResponse, 16)

            return {
                result: "OK",
                type: getTokNetwork !== "0" ? typed : "setup",
            };
        } else {
            return {
                result: "OK",
                type: "setup"
            };
        }
    } catch (e) {
        return {
            result: "FAIL",
            error: e.message
        };
    }
}

const getNetworkFeeAsync = async(network, toNetwork, type) => {
    networkSettings(network);

    try {
        const provider = new ethers.providers.JsonRpcProvider(rpcUrl);
        const toNetworkHex = utils_textToHex(toNetwork);

        const bridgeConfigContract = new ethers.Contract(
            bridgeConfigContractAddress,
            abiBridgeConfig,
            provider
        );

        const networkIdx = await bridgeConfigContract.getNetworkFeeIdxByName(toNetworkHex);

        if (networkIdx === ethers.constants.MaxUint32) {
            throw "not found";
        }

        const networkFee = await bridgeConfigContract.getNetworkFeeByIdx(networkIdx);

        let resultFee;

        switch (type) {
            case "nft":
                resultFee = networkFee.nftFee;
                break;
            case "token":
                resultFee = networkFee.tokenFee;
                break;
            case "setup":
                resultFee = networkFee.regFee;
                break;
            default:
                throw new Error("Invalid main network type");
        }

        return {
            result: "OK",
            networkFee: resultFee.toString()
        };

    } catch (e) {
        console.log(e);
        return {
            result: "FAIL",
            error: e.message
        };
    }
}

const decimalToHexString = (decimalValue) => {
    return '0x' + decimalValue.toString(16);
  }

module.exports = {
    networkSettings,
    utils_textToHex,
    getNodeHomeAsync,
    getNetworkFeeAsync,
    decimalToHexString
};